import { randomUUID } from 'crypto'
import { postRepositoryInMemory, publishPost, signUp, userRepositoryInMemory } from '../../../common/__test__/GenUserForTest'
import UpvotePost from '.'
import GetPost from '../GetPost'

let upvotePost: UpvotePost
let getPost: GetPost

beforeEach(() => {
  upvotePost = new UpvotePost(postRepositoryInMemory, userRepositoryInMemory)
  getPost = new GetPost(postRepositoryInMemory)
})

test('não deve ser possível dar upvote no post se o post não for encontrado', async () => {
  const user = await signUp()

  const input = {
    userId: user.userId,
    postId: randomUUID(),
  }
  await expect(() => upvotePost.handle(input)).rejects.toThrow(
    'Conteúdo não encontrado',
  )
})

test('não deve ser possível dar upvote no post se o você já deu upvote', async () => {
  const post = await publishPost()
  const user = await signUp()

  const input = {
    userId: user.userId,
    postId: post.postId,
  }
  await upvotePost.handle(input)
  await expect(() => upvotePost.handle(input)).rejects.toThrow(
    'Conteúdo não encontrado',
  )
})

test('deve ser possível dar upvote no post', async () => {
  const post = await publishPost()
  const user = await signUp()

  const input = {
    userId: user.userId,
    postId: post.postId,
  }
  await upvotePost.handle(input)
  const output = await getPost.handle({ postId: input.postId })
  expect(output.upvotes).toStrictEqual(1)
})

